
public class BinderTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int value=10;
		int mid=value >>1;
		System.out.println("value>>1="+mid);
		int dule=value <<1;
		System.out.println("value<<1="+dule);
		int huo=mid^dule;
		System.out.println("mid^dule="+huo);
		
	}

}
